class Counter extends Component {
  increaseHandler() {
    this.props.increase();
  }

  decreaseHandler() {
    this.props.decrease();
  }

  toggleCounterHandler() {
    // this.setState((prevState) => {
    //   return { showCounter: !prevState.showCounter };
    // });
  }

  render() {
    return (
      <main className={classes.counter}>
        <h1>Redux Counter</h1>
        <div className={classes.value}>{this.props.counter}</div>
        <div>
          <button onClick={this.incrementHandler.bind(this)}>Increase</button>
          <button onClick={this.decrementHandler.bind(this)}>Decrease</button>
        </div>
        <button onClick={this.toggleCounterHandler.bind(this)}>Toggle Counter</button>
      </main>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    counter: state.counter,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    increase: () => dispatch({ type: "INCREASE" }),
    decrease: () => dispatch({ type: "DECREASE" }),
    toggleCounterHandler: () => dispatch({ type: "TOGGLE" }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Counter);

