import { useSelector, useDispatch } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useEffect } from "react";
import Notification from "./components/UI/Notification";
import { uiActions } from "./store/uiSlice";

function App() {
  const cart = useSelector((state) => state.cart);
  const show = useSelector((state) => state.ui.showCart);
  const dispatch = useDispatch();

  useEffect(() => {
    const url = "https://react-redux-8f26c-default-rtdb.firebaseio.com/cart.json";
    const config = {
      method: "PUT",
      body: JSON.stringify(cart),
    };
    const sendCartData = async () => {
      dispatch(uiActions.showNotification({ status: "pending", title: "Sending...", message: "Sending cart data." }));

      <Notification title="Sending data..." message />;
      try {
        const response = await fetch(url, config);

        if (!response.ok) {
          throw new Error("Sending cart data failed.");
        }

        const data = await response.json();

        return data;
      } catch (error) {
        console.log(error.message);
      }
    };
    sendCartData();
    dispatch(
      uiActions.showNotification({ status: "success", title: "Success!", message: "Sent cart data successfully." })
    );
  }, [cart]);

  return (
    <Layout>
      {show && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
